import { Component } from '@angular/core';
import {FormGroup, FormBuilder, AbstractControl, FormControl} from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

  signupForm:FormGroup = new FormGroup({
    fullname: new FormControl(''),
    email: new FormControl(''),
    pwd: new FormControl(''),
    mobile:new FormControl('')
});
  constructor(private serv1:MyserviceService,private formBuilder: FormBuilder, private route : Router,private http:Http){}

  ngOnInit(){
      this.signupForm = this.formBuilder.group({
          fullname: [''],
          email:[''],
          pwd:[''],
          mobile:['']
      });

  }
  signUp(){

      const fullname1 = this.signupForm['fullname']
      const email1 = this.signupForm['email']
      const pwd = this.signupForm['password']
      const mobile1 = this.signupForm['mobile']

      const data ={fullname:fullname1 , email:email1 , pwd:pwd,mobile:mobile1}
      this.serv1.userobj.push(data);
      alert("New User is registered !")

      console.log(data)
      this.route.navigate(['/Login'])
  }

  get func():{[m:string]:AbstractControl}
  {
      return this.signupForm.controls;
  }

}
