import { Component } from '@angular/core';
import {FormGroup, FormBuilder} from '@angular/forms';
import { Router } from '@angular/router';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

  public signupForm! : FormGroup;
  constructor(private serv1:My,private formBuilder: FormBuilder, private route : Router){}

  ngOnInit():void{
      this.signupForm = this.formBuilder.group({
          fullname: [''],
          email:[''],
          password:[''],
          mobile:['']
      });

  }
  signUp(){
      const fullname1 = this.formBuilder.control['fullname'].value
      const email1 = this.formBuilder.control['email'].value
      const pwd = this.formBuilder.control['password'].value
      const mobile1 = this.formBuilder.control['mobile'].value

      const data = {fullname:fullname1, email:email1, password:pwd, mobile:mobile1}
      this.serv1.userobj.push(data);
  }
}
