import { Component } from '@angular/core';
import { Employee } from '../Employee';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'input-output';

    educationOptions = [
      '1oth pass',
      'Diploma',
      'Graduate',
      'Post Graduate',
      'PHD',
    ];
  Empdata?:Employee[]=[];
  rec?:Employee|any;

    emp_id?:number;
    emp_nm?:string;
    educat?:string;
    gender?:string;
    com_nm?:string;
    expr?:string;
    salr?:number;


    addEmployee(){
        this.Empdataa?.push({
          empid:this.emp_id,
          empnm:this.emp_nm,
          gen:this.gender,
          edu:this.educat,
          compnm:this.com_nm,
          exp:this.expr,
          sal:this.salr
        })


    }
    delempdt(e:any)
    {
      this.Empdata?.splice(this.Empdata?.indexOf(e),1)
    }
}
