import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
   loginForm !:FormGroup ;

  constructor(private route:Router, private http:HttpClient, private serv1:MyserviceService,private formbuilder:FormBuilder){}

  ngOnInit():void{
      this.loginForm = new FormGroup({

        'email':new FormControl(),
        'pwd':new FormControl()
      });

  }

  login():void{
    this.http.get<any>("http://localhost:3000/signupUser").subscribe(
      res => {
       const user = res.find((a:any) => {
        return  a.email ===  this.loginForm.value.email   &&  a.pwd === this.loginForm.value.pwd ;
       })
       if(user){
        alert("login succesful !");
        console.log(this.loginForm.value);
        this.loginForm.reset();
        this.route.navigate(['/Home']);
       }else{
        alert("user not found");
        this.route.navigate(['/Login']);
       }
      },error => {
        alert("Something went wrong");
      }
    );
  }
}

<app-header></app-header>

<div class="container">
    <div class="row">
      <div class="col-md-6">
        <div class="card">
          <div class="text-center">
            <h1>Login</h1>
            <h6>Please enter email & password</h6>
          </div>

          <form class="form-group" [formGroup]="loginForm" (ngSubmit) ="login()">
              <div class="mb-3 ">
                <label for="exampleInputEmail1" class="form-label">Email address</label>

                <input type="email" formControlName="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
                <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
              </div>
              <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Password</label>
                <input type="password" formControlName="pwd"  class="form-control" id="exampleInputPassword1" Input>
              </div>
              <div class="mb-3 form-check">
                <input type="checkbox" class="form-check-input" id="exampleCheck1">
                <label class="form-check-label" for="exampleCheck1">Check me out</label>
              </div>
              <div class="text-center">
                <button type="button" class="btn btn-outline-primary btn-lg ">Login</button>
              </div>
            </form>

          <a style="color:#fff ; margin-top : 10px" routerLink="/SignUp">New user? Click to signup!!</a>
        </div>
      </div>
    </div>
</div>
