import { Component } from '@angular/core';
import { Product } from '../Product';
import { MyserviceService } from './myservice.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'servicexample';
  data?:Product[]


  obj=[{name:'headset'},{name:'pen'},{name:'books'},{name:'pencils'},];
    order=0
  //constructor DI
  constructor(private serv:MyserviceService)
  {
    console.log(serv.usr);
    this.order++;
    console.log("constructor from app component get called");

  }


  delname(a:any){
    this.obj.splice(this.obj.indexOf(a),1)
  }

  ngOnChanges() {
    console.log("ngOnChanges from App component. Order #"+ this.order);
    this.order++;
}
ngOnInit() {
   console.log("ngOnInit from App component. Order #"+ this.order);
   this.order++;
}
ngDoCheck() {
   console.log("DoCheck from App component. Order #"+ this.order);
   this.order++;
}
ngAfterContentInit() {
   console.log("ngAfterContentInit from App component. Order #"+ this.order);
   this.order++;
}
ngAfterContentChecked() {
   console.log("AfterContentChecked from App component. Order #"+ this.order);
   this.order++;
}
ngAfterViewInit() {
    console.log("AfterViewInit from App component. Order #"+ this.order);
    this.order++;
}
ngAfterViewChecked() {
     console.log("AfterViewChecked from App component. Order #"+ this.order);
     this.order++;
}
ngOnDestroy() {
  console.log("ngOnDestroy from App component. Order #"+ this.order);
  this.order++;
}

id?:number
nm?:string
cat?:string

callendpoint()
  {
    this.serv.getallproducts().subscribe((r:Product |any)=>{console.log(r);this.data=r;})

  }
  addnewrecord()
      {
        this.serv.createRecords(this.id,this.nm,this.cat).subscribe(res=>{console.log(res)})
      }

      deleterecord()
      {
        this.serv.deleterec(this.id).subscribe((res: any)=>{console.log(res)})
      }



}
<app-headercompo></app-headercompo>

<button type="button" (click)="callendpoint()"> Call Service</button>

Enter id: <input type="text" [(ngModel)]="id" name="id"/><br>
Enter name: <input type="text" [(ngModel)]="nm" name="nm"/><br>
Enter cat: <input type="text" [(ngModel)]="cat" name="cat"/><br>

<button type="button" (click)="addnewrecord()"> Add New Record</button>

<hr>
Enter Delete record: <input type="text" [(ngModel)]="id" name="id"/><br>
<button type="button" (click)="deleterecord()"> Delete Record</button>
<hr>
<ul>
<li *ngFor="let a of data">{{ a.id}} - {{a.name}} - {{a.category }}</li>

</ul>

<hr>

<app-hookexample [fetchdata]="obj" (senditem)="delname($event)">
  <strong class="app">this is a app data on child component</strong>
</app-hookexample>
