import { Component } from '@angular/core';
import {FormGroup, FormBuilder, AbstractControl, FormControl} from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

 public signupForm !:FormGroup ;
  constructor(private serv1:MyserviceService,private formBuilder: FormBuilder, private route : Router, private http:Http){}

  ngOnInit():void{
      this.signupForm = this.formBuilder.group({
          fullname: [''],
          email:[''],
          pwd:[''],
          mobile:['']
      });

  }
  signUp(){

      this.http.post<any>("http://localhost:3000/signupUser",this.signupForm.value)
      .subscribe(res=>{
        alert("Regitration Successfull");
        this.signupForm.reset();
      })
  }




  get func():{[m:string]:AbstractControl}
  {
      return this.signupForm.controls;
  }

}
