import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
   loginForm !:FormGroup ;

  constructor(private router:Router, private http:HttpClient, private serv1:MyserviceService,private formbuilder:FormBuilder){}

  ngoninit():void{
      this.loginForm = this.formbuilder.group({

        'email':[''],
        'pwd':['']
      });

  }

  login(){
    this.http.get<any>("http://localhost:3000/signupUser").subscribe(
      res => {
       const user = res.find((a:any) => {
        return  a.email ===  a.login.value.email   &&  a.pwd === a.login.value.pwd ;
       })
       // console.log(this.loginForm.value);
        this.loginForm.reset();
        this.router.navigate(['/signUp']);
      },
      error => {
        alert("Something went wrong");
      }
    );
  }
}
