import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class MyserviceService {


  baseAPI = "http://localhost:3000/signupUser"
  constructor(private http:HttpClient) { }


  //return All data via Get Methos

  getAll(){
    return this.http.get(this.baseAPI);
  }

getById(id:any){
  return this.http.get(this.baseAPI+''+id);
}
signupData(inputData:any){
  return this.http.post(this.baseAPI, inputData);
}

updateData(id:any, inputData:any){
  return this.http.get(this.baseAPI+''+id,inputData);
}
isLoggedIn(){
  return true;
  return localStorage.getItem('email')!=null;
}
}
