import { Component, DoCheck } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements DoCheck {

  isMenuRequire = false;
  title = 'input-output';

  constructor(private router:Router){}
  ngDoCheck(): void {
      let currentUrl = this.router.url;
      if(currentUrl == '/Login' || currentUrl == 'SignUp'){
        this.isMenuRequire=false;
      }else{
        this.isMenuRequire=true;
      }
  }
}
<nav class="navbar navbar-dark bg-light justify-content-center"  *ngIf="isMenuRequire">
  <div class="container-fluid">
    <a class="navbar-brand" href="#" style="color: RGB(146, 168, 209);">Employee Management Details</a>

    <ul class="nav justify-content-end" >
      <li class="nav-item">
        <a class="nav-link active"  aria-current="page" routerLink="/Home" href="#">Add Employee</a>
      </li>
      <li class="nav-item">
        <a class="nav-link active" aria-current="page" routerLink="/about" href="#">About</a>
      </li>
      <li class="nav-item">
        <a class="nav-link active" aria-current="page" routerLink="/contactus" href="#">ContactUs</a>
      </li>
      <li class="nav-item">
        <a class="nav-link active" aria-current="page" routerLink="/Login" href="#">Login</a>
      </li>
      <li class="nav-item">
        <button class="btn btn-outline-primary" routerLink="/Login"   type="submit">Logout</button>
      </li>

    </ul>
  </div>

</nav>
