import { Component } from '@angular/core';
import {FormGroup, FormBuilder, AbstractControl, FormControl, Validators} from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

 public signupForm !:FormGroup ;
  submitted = false
  constructor(private serv1:MyserviceService,private formBuilder: FormBuilder, private router : Router, private http:HttpClient){}

  ngOnInit():void{

      this.signupForm = this.formBuilder.group(
        {
          fullname: ['',[Validators.required,Validators.minLength(6)]],
          email:['',[Validators.required,Validators.email]],
          pwd:['',[Validators.required,Validators.minLength(8)]],
          cpwd:['',[Validators.required]],
          mobile:['',[Validators.required,Validators.pattern("^[0-9]*$"),Validators.minLength(10),Validators.maxLength(10)]]
      }
      );
  }
  get func():{[m:string]:AbstractControl}
  {
      return this.signupForm.controls;
  }
  signUp(){
      this.submitted=true;
      if(this.signupForm.invalid){
        return
      }
      this.http.post<any>("http://localhost:3000/signupUser",this.signupForm.value).subscribe(res=>{
        alert("Regitration Successful !");
        console.log(this.signupForm.value);
        this.signupForm.reset();
        this.router.navigate(['/Login']);
      },err=>{
        alert("Something went wrong");
      });
  }






}
<app-header></app-header>
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <div class="card">
        <div class="text-center">
          <h1>Sign Up</h1>
          <h6>Register Yourself!</h6>
        </div>

            <form class="form-group" [formGroup]="signupForm" (ngSubmit) ="signUp()">
            <div class="mb-3">
              <label for="exampleInputFullname" class="form-label">Full Name</label>
              <input type="text" formControlName="fullname" class="form-control" id="exampleInputFullname"
              [ngClass]="{'is-invalid': submitted && func['fullname'].errors}"/>
              <div *ngIf="submitted && func['fullname'].errors" class="invalid-feedback">
              <div *ngIf="submitted && func['fullname'].errors['required']" > Full name is required</div>
              <div *ngIf="submitted && func['fullname'].errors['minLength']"> fullname length should be minimum 6 character letters</div>
              <div *ngIf="submitted && func['fullname'].errors['namenotmatch']" > Full name can only alphabet letters not a numbers</div>
            </div>
            </div>

            <div class="mb-3">
              <label for="exampleInputMobileNumber" class="form-label">Mobile Number</label>
              <input type="number" [ngClass]="{'is-invalid': submitted && func['mobile'].errors}" formControlName="mobile"class="form-control" id="exampleInputMobileNumber">
              <div *ngIf="submitted && func['mobile'].errors" class="invalid-feedback">
                  <div *ngIf="submitted && func['mobile'].errors['required']">phone number is required</div>
                  <div *ngIf="submitted && func['mobile'].errors['pattern']" >phone number is required</div>
                  <div *ngIf="submitted && func['mobile'].errors['pattern'] ||submitted && func['mobile'].errors['maxLength'] || submitted && func['mobile'].errors['minLength']">Phone number must be at least 10 numbers </div>
              </div>

            </div>
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">Email address</label>
              <input type="email" [ngClass]="{'is-invalid': submitted && func['email'].errors}"  formControlName="email"  class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
              <div *ngIf="submitted && func['fullname'].errors" class="invalid-feedback"> Email id is required</div>
              <div id="emailHelp" class="form-text"><span>We'll never share your email with anyone else.</span></div>
            </div>
            <div class="mb-3">
              <label for="exampleInputPassword1" class="form-label">Password</label>
              <input type="password" [ngClass]="{'is-invalid': submitted && func['pwd'].errors}" formControlName="pwd"  class="form-control" id="exampleInputPassword1">
              <div *ngIf="submitted && func['fullname'].errors" class="invalid-feedback"> password is required</div>
            </div>
            <div class="mb-3">
              <label for="exampleInputConfirmPassword1" class="form-label">Confirm Password</label>
              <input type="password" [ngClass]="{'is-invalid': submitted && func['cpwd'].errors}" formControlName="cpwd"  class="form-control" id="exampleInputConfirmPassword1">
              <div *ngIf="submitted && func['fullname'].errors" class="invalid-feedback"> confirm password is required</div>
            </div>
            <div class="text-center">
            <button type="submit" class="btn btn-outline-danger btn-lg text">SignUp</button>
          </div>
          </form>

        <a style="color:#fff; margin-top : 10px" routerLink="/Login">Already registered?   Click to Login !!</a>
      </div>
    </div>
  </div>
</div>
