import { Component } from '@angular/core';

@Component({
  selector: 'app-contactus',
  templateUrl: './contactus.component.html',
  styleUrls: ['./contactus.component.css']
})
export class ContactusComponent {
  FormData!: FormGroup;
  form = {
    FromName: '',
    ContactFromEmail: '',
    ContactNumber:'',
    message:''
  };

  @ViewChild('f') f:any;

  IsContactDetailSent:boolean=false;

  constructor(private SpinnerService: NgxSpinnerService,private service: HttpService, private notify:NotificationService, private toster:ToastrService) { }

  ngOnInit(): void {
    this.IsContactDetailSent=false;
  }

  onSubmit(form: NgForm): void {
    this.SpinnerService.show();
    console.log(JSON.stringify(this.form, null, 2));
    this.service.httpPostRequest(`${environment.baseURL}api/notification/contactus`,this.form).subscribe((response: any) => {
      console.log(response);
      this.SpinnerService.hide();
      this.toster.success("Thank you for contacting resolvers team. You will receive an acknowledgement in your inbox.We will respond you shortly");
      form.resetForm();
      this.IsContactDetailSent=true;
    });
  }

}
