import { Component } from '@angular/core';
import {FormGroup, FormBuilder, AbstractControl, FormControl, Validators} from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

 public signupForm !:FormGroup ;

  constructor(private serv1:MyserviceService,private formBuilder: FormBuilder, private router : Router, private http:HttpClient){}

  ngOnInit():void{

      this.signupForm = this.formBuilder.group(
        {
          fullname: [''],
          email:[''],
          pwd:[''],
          cpwd:[''],
          mobile:['']
      }
      );
  }
  get func():{[m:string]:AbstractControl}
  {
      return this.signupForm.controls;
  }
  signUp(){

      this.http.post<any>("http://localhost:3000/signupUser",this.signupForm.value).subscribe(res=>{
        alert("Regitration Successful !");
        console.log(this.signupForm.value);
        this.signupForm.reset();
        this.router.navigate(['/Login']);
      },err=>{
        alert("Something went wrong");
      });
  }






}
<app-header></app-header>
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <div class="card">
        <div class="text-center">
          <h1>Sign Up</h1>
          <h6>Register Yourself!</h6>

        </div>


            <form class="form-group" [formGroup]="signupForm" (ngSubmit) ="signUp()">
            <div class="mb-3">
              <label for="exampleInputFullname" class="form-label">Full Name</label>
              <div class="input-field">
                <span  class="fa fa-user p-2"></span>
              <input type="text" formControlName="fullname" class="form-control" id="exampleInputFullname"
              [ngClass]="{'is-invalid': submitted && func['fname'].errors}" />
            </div>
            </div>
            <div class="mb-3">
              <label for="exampleInputMobileNumber" class="form-label">Mobile Number</label>
              <input type="number" formControlName="mobile"   class="form-control" id="exampleInputMobileNumber">
            </div>
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">Email address</label>
              <input type="email" formControlName="email"  class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
              <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
            </div>
            <div class="mb-3">
              <label for="exampleInputPassword1" class="form-label">Password</label>
              <input type="password" formControlName="pwd"  class="form-control" id="exampleInputPassword1">
            </div>
            <div class="mb-3">
              <label for="exampleInputConfirmPassword1" class="form-label">Confirm Password</label>
              <input type="password" formControlName="cpwd"  class="form-control" id="exampleInputConfirmPassword1">
            </div>
            <div class="text-center">
            <button type="button" class="btn btn-outline-danger btn-lg text">SignUp</button>
          </div>
          </form>

        <a style="color:#fff; margin-top : 10px" routerLink="/Login">Already registered?   Click to Login !!</a>
      </div>
    </div>
  </div>
</div>
