import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup, ValidationErrors, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  submitted = false;
   loginForm !:FormGroup ;

  constructor(private route:Router, private http:HttpClient, private serv1:MyserviceService,private formbuilder:FormBuilder){}

  ngOnInit():void{
      this.loginForm = this.formbuilder.group({

        'email':['',[Validators.required,Validators.email]],
        'pwd':['',[Validators.required,Validators.minLength(8)]]
      });

  }

  get func():{[m:string]:AbstractControl}
  {
      return this.loginForm.controls;
  }


  login():void{
    this.submitted = true
    if(this.loginForm.invalid){
      return
    }
    this.http.get<any>("http://localhost:3000/signupUser").subscribe(
      res => {
       const user = res.find((a:any) => {
        return  a.email ===  this.loginForm.value.email   &&  a.pwd === this.loginForm.value.pwd ;
       })
       if(user){
        alert("login succesful !");
        console.log(this.loginForm.value);
        this.loginForm.reset();
        this.route.navigate(['/Home']);
       }else{
        alert("user not found");
        this.route.navigate(['/Login']);
       }
      },error => {
        alert("Something went wrong");
      }
    );
  }
}

<app-header></app-header>

<div class="container">
    <div class="row">
      <div class="col-md-6">
        <div class="card">
          <div class="text-center p-3">
            <h1 class="card-header">Login Form<br><h6>Please enter email & password !</h6></h1>

          </div>

          <form class="form-group" [formGroup]="loginForm" (ngSubmit) ="login()">
              <div class="mb-3 ">
                <label for="exampleInputEmail1" class="form-label">Email address</label>
                <input type="email" [ngClass]="{'is-invalid': submitted && func['email'].errors}" formControlName="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
                <div *ngIf="submitted && func['email'].errors" class="invalid-feedback">
                  <div *ngIf="func['email'].errors['required']">Email id is required</div>
                  <div *ngIf="func['email'].errors['email']">Email ID is not correct</div>
                </div>
              </div>
                <div id="emailHelp" class="form-text"><span>We'll never share your email with anyone else.</span></div>
              <br>
              <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">Password</label>
                <input type="password" [ngClass]="{'is-invalid': submitted && func['pwd'].errors}" formControlName="pwd"  class="form-control" id="exampleInputPassword1" required />
                <div *ngIf="submitted && func['pwd'].errors" class="invalid-feedback">
                  <div *ngIf="submitted && func['pwd'].errors['required']">password is required</div>
                  <div *ngIf="submitted && func['pwd'].errors['minlength']">password should be minimum 8 character</div>
                  <div *ngIf="submitted && func['pwd'].errors['maxlength']">password should be maximum 15 character</div>
                </div>

              </div>
              <div class="mb-3 form-check">
                <input type="checkbox" class="form-check-input" id="exampleCheck1">
                <label class="form-check-label" for="exampleCheck1">Check me out</label>
              </div>
              <br>
            <span> New user?  <a style="color:#2019f073 ; margin-top : 20px" routerLink="/SignUp">Click to signup!!</a></span>
              <br><br>
              <div class="text-center">
                <button type="submit" class="btn btn-outline-primary btn-lg ">Login</button>

            </div>
            </form>


          <br>

        </div>
      </div>
    </div>
</div>
