import { Component } from '@angular/core';
import {FormGroup, FormBuilder, AbstractControl, FormControl, Validators, ValidationErrors, ValidatorFn} from '@angular/forms';
import { Router } from '@angular/router';
import { MyserviceService } from '../myservice.service';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {

 public signupForm !:FormGroup ;
  submitted = false
  constructor(private serv1:MyserviceService,private formBuilder: FormBuilder, private router : Router, private http:HttpClient){}
      //Hook  function
  ngOnInit():void{
        //object
      this.signupForm = this.formBuilder.group(
        {
          fullname: ['',[Validators.required,Validators.minLength(6),this.customename()]],
          email:['',[Validators.required,Validators.email]],
          pwd:['',[Validators.required,Validators.minLength(8)]],
          cpwd:['',[Validators.required,Validators.minLength(8)]],
          mobile: ['', [ Validators.required,Validators.pattern("^((\\+91-?)|0)?[0-9]{10}$")]]
      },
      {
        validators: this.matchpassword
      }
      );
  }

  //validations
  get func():{[m:string]:AbstractControl}
  {
      return this.signupForm.controls;
  }
  customename(){
    return (ctrl:AbstractControl) : ValidationErrors | null =>
    {
      var regexpression = /^[a-zA-Z]+$/
      if( ! regexpression.test(ctrl.value))
              {
                return {namenotmatch:true}
              }

      return null
    };
  }
    export const matchpassword : ValidatorFn = (ctrl:AbstractControl):ValidationErrors | null =>
  {
      return (ctrl:AbstractControl) : ValidationErrors | null =>
    {
        let pwd = ctrl.get('pwd')
        let cpwd = ctrl.get('cpwd')

        if(  pwd && cpwd && pwd?.value !== cpwd?.value){
          return { passnotmatch : true}
        }
        return null
    }

}
get passwordMatchError() {
  return (
    this.signupForm.getError('mismatch') &&
    this.signupForm.get('cfrmpwd')?.touched
  );
}
  keyPress(event: any) {
    const pattern = /[0-9\+\-\ ]/;
    let inputChar = String.fromCharCode(event.charCode);
    if (event.keyCode != 8 && !pattern.test(inputChar)) {
      event.preventDefault();
    }
  }


  //jsp server used navigate another page
  signUp(){
      this.submitted=true;
      if(this.signupForm.invalid){
        return
      }
      this.http.post<any>("http://localhost:3000/signupUser",this.signupForm.value).subscribe(res=>{
        alert("Regitration Successful !");
        console.log(this.signupForm.value);
        this.signupForm.reset();
        this.router.navigate(['/Login']);
      },err=>{
        alert("Something went wrong");
      });
  }






}
